function mquig(i){return location.search.split(i)[1].split("&")[0].split("?")[0]}setTimeout((function(){if(location.search.indexOf("t=")>-1){let i=mquig("t=");if(byId(i).scrollIntoView(),location.search.indexOf("bg=")>-1){let t=mquig("bg=");byId(i).style.backgroundColor=t}}else if(location.href.indexOf("#")>-1){let i=location.href.split("#")[1].split("&")[0].split("?")[0];byId(i).scrollIntoView()}}),10);